import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {

  const [data, setData] = useState({
    initData: [],
    subs: [],
    changeMonth: [],
    changeYear: [],
  });

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {

        let subs = data.data.map((e) => e.new_subscriptions);

        let res =[]
        for(var i=0;i<subs.length;i++){
          res.push( subs[i] + subs[i] )
        }

        setData((prev) => ({
          ...prev,
          initData: data.data,
          subs:subs
        }));

      });
  }, []);



  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {typeof data === "undefined" ? (
            <p>Loading...</p>
          ) : (
            data.initData.map((data, i) => (
              <tr key={i}>
                <td key={i}>{moment(data.date).format("MMM yyyy")}</td>
                <td>{data.new_subscriptions}</td>
                <td>Change from last month (%)</td>
                <td>Change from last year (%)</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default App;
