import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [data, setData] = useState({
    initData: [],
    subs: [],
    changeMonth: [],
    changeYear: [],
  });

  const [newdata, setNewData] = useState([
    { subs: [], changeMonth: [], changeYear: [] },
  ]);

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {
        let subs = data.data.map((e) => e.new_subscriptions);

        let newMonth = [0];
        let newYear = [0];
        for (var i = 0; i < subs.length - 1; i++) {
          const month = (subs[i + 1] * 100) / subs[i] - 100;
          newMonth.push(+month.toFixed(2));
          const years = (subs[i + 12] * 100) / subs[i] - 100;
          newYear.push(+years.toFixed(2) || 0);
        }

        setData((prev) => ({
          ...prev,
          initData: data.data,
          subs: subs,
          changeMonth: newMonth,
          changeYear: newYear,
        }));

        setNewData((prev) => [
          {
            ...prev,
            subs: subs,
            changeMonth: newMonth,
            changeYear: newYear,
          },
        ]);


      });
  }, []);

  const Month = () => {
    return <td>blbl</td>;
  };

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {data.initData.map((data, i) => (
            <tr key={i}>
              <td>{moment(data.date).format("MMM yyyy")}</td>
              <td>{data.new_subscriptions}</td>
              <td>Change from last month (%)</td>
              <td>Change from last year (%)</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
