import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [backendData, setBackendData] = useState();

  const [data, setData] = useState({
    dates: [],
    subs: [],
    changeMonth: [],
    changeYear: [],
  });

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {
        setBackendData(data.data);
      });
  }, []);

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {
           backendData 
          }
           
              <tr>
                <td>{moment(data.date).format("MMM yyyy")}</td>
                <td>{data.new_subscriptions}</td>
                <td>Change from last month (%)</td>
                <td>Change from last year (%)</td>
              </tr>
            
       
        </tbody>
      </table>
    </div>
  );
};

export default App;
