import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [data, setData] = useState({
    initData: [],
    subs: [],
    changeMonth: [],
    changeYear: [],
  });

  const [newData, setNewData] = useState([
    {date:[],subs: [], changeMonth: [], changeYear: [] },
  ]);
  

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {
        let date = data.data.map((d)=> d.date)
        let subs = data.data.map((e) => e.new_subscriptions);

        let newMonth = [0];
        let newYear = [0];
        for (var i = 0; i < subs.length - 1; i++) {
          const month = (subs[i + 1] * 100) / subs[i] - 100;
          newMonth.push(+month.toFixed(2));
          const years = (subs[i + 12] * 100) / subs[i] - 100;
          newYear.push(+years.toFixed(2) || 0);
        }


        setNewData(([{
          date:date,
          subs: subs,
          changeMonth: newMonth,
          changeYear: newYear,
        }]));

      });
  }, []);

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
{newData.map((data,i)=>(
            <tr>

            {data.date.map((d)=>(

              <th>{moment(d).format("MMM YYYY")}</th>
            ))}
            {data.subs.map((m)=>(

              <th>{m}</th>
            ))}
              <td>subs</td>
              <td>Change from last month (%)</td>
              <td>Change from last year (%)</td>
            </tr>
))}


        </tbody>
      </table>
    </div>
  );
};

export default App;
