import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [data, setData] = useState({
    initData: [],
    subs: [],
    changeMonth: [],
    changeYear: [],
  });

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {
        let subs = data.data.map((e) => e.new_subscriptions);

        let newAvrege = [];
        for (var i = 0; i < subs.length - 1; i++) {
          const res = (subs[i + 1] * 100) / subs[i] - 100;
          newAvrege.push(res.toFixed(2));
        }

        console.log(newAvrege);

        setData((prev) => ({
          ...prev,
          initData: data.data,
          subs: subs,
          changeMonth:newAvrege
        }));
      });
  }, []);

  const Month = () => {
   
    return <tr>blbl</tr>
  };

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {data.initData.map((data, i) => (
            <tr key={i}>
              <td>{moment(data.date).format("MMM yyyy")}</td>
              <td>{data.new_subscriptions}</td>
              <Month />
              <td>Change from last year (%)</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
