import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [backendData, setBackendData] = useState([{}]);
  const [filters, setFilters] = useState({
    data: [],
    newSub: [],
    changeFromLastMonth: [],
    changeFromLastYear: [],
  });

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {
        setBackendData(data.data);
      });
  }, []);

  useEffect(() => {
    let newSubArray = backendData.map((d) => d.new_subscriptions);
    setFilters((prev) => ({
      ...prev,
      data: backendData,
      newSub: newSubArray,
    }));
  }, [backendData]);

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {typeof backendData === "undefined" ? (
            <p>Loading...</p>
          ) : (
            backendData.map((data, i) => (
              <tr key={i}>
                <td>{moment(data.date).format("MMM yyyy")}</td>
                filters.newSub.map((d)=>({}))
                <td>{d.newSub}</td>

                
                <td>{}</td>
                <td>Change from last year (%)</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default App;
