import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [filters, setFilters] = useState({
    backData: [],
    date: "",
    NewSubscriptions: "",
    changeLastMonth: "",
    changeLastYear: "",
  });

  const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {
        setFilters((prev) => ({
          ...prev,
          backData: data.data,
        }));
      });
  }, []);

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {typeof filters === "undefined" ? (
            <p>Loading...</p>
          ) : (
            backendData.map((data, i) => (
              <tr key={i}>
                <td>{moment(data.date).format("MMM  YYYY")}</td>
                <td>{data.new_subscriptions}</td>
                <td>Change from last month(%)</td>
                <td>Change from last year (%)</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default App;
