import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [backendData, setBackendData] = useState([{}]);
  const [filters, setFilters] = useState({
    months: {
      "01": "Jan",
      "02": "Feb",
      "03": "Mar",
      "04": "Apr",
      "05": "May",
      "06": "Jun",
      "07": "Jul",
      "08": "Aug",
      "09": "Sep",
      "11": "Oct",
      "12": "Nov",
      "13": "Dec",
    },
  });

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {
        setBackendData(data.data);
      });
  }, []);

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {typeof backendData === "undefined" ? (
            <p>Loading...</p>
          ) : (
            backendData.map((data, i) => (
              <tr key={i}>
                <td>{data.date}</td>
                <td>{data.new_subscriptions}</td>
                <td>Change from last month(%)</td>
                <td>Change from last year (%)</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default App;
