import React from "react";
import moment from "moment";
import { useEffect } from "react";
import { useState } from "react";
import "../App.css";

const App = () => {
  const [data, setData] = useState({
    initData: [],
    subs: [],
    changeMonth: [],
    changeYear: [],
  });

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) => {

        let subs = data.data.map((e) => e.new_subscriptions);

        setData((prev) => ({
          ...prev,
          initData: data.data,
          subs: subs,
        }));
      });
  }, []);

  const Month = ()=>{

    for(var i=1;i<data.subs.length;i++){

    }
    return <td>{data.subs[i]}</td>

  }

  return (
    <div className="app-container">
      <table>
        <thead>
          <tr>
            <th>Month</th>
            <th>New Subscriptions</th>
            <th>Change from last month(%)</th>
            <th>Change from last year (%)</th>
          </tr>
        </thead>
        <tbody>
          {data.initData.map((data, i) => (
            <tr>
              <td key={i}>{moment(data.date).format("MMM yyyy")}</td>
              <td>{data.new_subscriptions}</td>
              <Month  />
              <td>Change from last year (%)</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
