  const [data, setData] = useState({
    initData: [],
    subs: [],
    changeMonth: [],
    changeYear: [],
  });

  const [newData, setNewData] = useState([
    {date:[],subs: [], changeMonth: [], changeYear: [] },
  ]);
  

